Date.prototype.toISO8601String = function() {
  return this.toISOString()
  .replace(/-/g, '')
  .replace(/:/g, '')
  .replace(/\.\d\d\d/, '');
}

Object.prototype.subset = function(array) {
  array = array || [];
  var _this = this;
  var copy = {};
  Object.keys(this).filter(function(key) {
    return array.indexOf(key) != -1;
  }).forEach(function(key) {
    copy[key] = _this[key];
  });
  return copy;
}
