Date.prototype.toISO8601String = function() {
  return this.toISOString()
  .replace(/-/g, '')
  .replace(/:/g, '')
  .replace(/\.\d\d\d/, '');
}

Object.defineProperty(Object.prototype, 'includeKeys', {
  get: function() {
    var _this = this;
    return function(array) {
      array = array || [];
      var copy = {};
      Object.keys(this).filter(function(key) {
        return array.indexOf(key) != -1;
      }).forEach(function(key) {
        copy[key] = _this[key];
      });
      return copy;
    }
  }
});

Object.defineProperty(Object.prototype, 'excludeKeys', {
  get: function() {
    var _this = this;
    return function(array) {
      array = array || [];
      var copy = {};
      Object.keys(this).filter(function(key) {
        return array.indexOf(key) == -1;
      }).forEach(function(key) {
        copy[key] = _this[key];
      });
      return copy;
    }
  }
});

Object.defineProperty(Object.prototype, 'hidden', {
  get: function() {
    var _this = this;
    return function(key, value) {
      Object.defineProperty(_this, key, {
        value: value,
        configurable: true,
      });
    }
  }
});

Object.defineProperty(String.prototype, 'lastChar', {
  get: function() {
    return this[this.length - 1];
  }
});

