module.exports = AwsDeploy;

var AwsDeployApi = require('./lib/aws_deploy_api');

/**
 * ### .AwsConfig()
 *
 * Configuration object for AwsDeploy
 *
 */

function AwsOpts() {
  this.aws_account = '';
  this.aws_region = '';
  this.aws_access_key_id = '';
  this.aws_secret_access_key = '';
}

/**
 * ### .AwsApiTree()
 *
 * Api definition tree
 *
 */

function AwsApiTree() {/*

  ### example
  
  ```javascript
  {
    name: 'Api Name',
    description: 'Api Description',
    root: {
      _methods: {
        GET: {
          // define integration point for GET /
        }
      }
      'users': {
        _methods: {
          GET: {
            // define integration point for GET /users
          }
        }
        ':id': {
          _methods: {
            GET: {
              // define integration point for GET /users/:id
            }
          }
        }
      }
    }
  }
  ```

*/}

/**
 * ### .AwsDeploy(opts)
 *
 * @api public
 * @param {AwsOpts} opts
 * @param {AwsApiTree} api
 *
 */

function AwsDeploy(config, api) {

  return AwsDeployApi(config, api);

}
