module.exports = AwsDeployApi;

var Promise = require('bluebird'); // available from parent's modules
var AwsApiGatewayApi = require('./aws_api_gateway_api');


function AwsDeployApi(config, api) {

  var apigw = new AwsApiGatewayApi(config);

  return new Promise(function(resolve, reject) {

    apigw.getRestApis()

    .then(function(apis) {

      console.log('APIS', apis);

    })

    .then(resolve)

    .catch(reject)

  });
  
}